// Your first C# program
using System;

class Program
{
    static void Main()
    {
        Console.WriteLine("Hello, World!");
        Console.WriteLine("Welcome to C# programming!");
    }
}

// Declaring and initializing variables
int age = 25;
double price = 19.99;
float temperature = 36.6f;
string name = "Alice";
char grade = 'A';
bool isStudent = true;

// Output
Console.WriteLine($"Name: {name}");
Console.WriteLine($"Age: {age}");
Console.WriteLine($"Is student: {isStudent}");

// Type conversion
int number = 10;
double result = number; // Implicit conversion

double pi = 3.14;
int roundedPi = (int)pi; // Explicit conversion (casting)

Console.WriteLine(roundedPi); // Output: 3

// Arithmetic operators
int a = 10;
int b = 3;

Console.WriteLine(a + b);  // 13
Console.WriteLine(a - b);  // 7
Console.WriteLine(a * b);  // 30
Console.WriteLine(a / b);  // 3 (integer division)
Console.WriteLine(a % b);  // 1 (remainder)

// Comparison and logical operators
int age = 20;
bool hasLicense = true;

bool canDrive = (age >= 18) && hasLicense;
Console.WriteLine($"Can drive: {canDrive}"); // true

bool isTeenOrAdult = (age >= 13) || (age >= 18);
Console.WriteLine($"Teen or Adult: {isTeenOrAdult}"); // true

// Simple if-else
int age = 20;

if (age >= 18)
{
    Console.WriteLine("You are an adult");
}
else
{
    Console.WriteLine("You are a minor");
}

// Multiple conditions with else if
int score = 85;

if (score >= 90)
{
    Console.WriteLine("Grade: A");
}
else if (score >= 80)
{
    Console.WriteLine("Grade: B");
}
else if (score >= 70)
{
    Console.WriteLine("Grade: C");
}
else
{
    Console.WriteLine("Grade: F");
}

// Nested if statements
int temperature = 25;
bool isSunny = true;

if (temperature > 20)
{
    if (isSunny)
    {
        Console.WriteLine("Perfect day for a picnic!");
    }
    else
    {
        Console.WriteLine("Warm but cloudy");
    }
}

// For loop
for (int i = 0; i < 5; i++)
{
    Console.WriteLine($"Count: {i}");
}
// Output: 0, 1, 2, 3, 4

// While loop
int number = 1;

while (number <= 5)
{
    Console.WriteLine($"Number: {number}");
    number++;
}

// Break and continue
for (int i = 0; i < 10; i++)
{
    if (i == 3)
    {
        continue; // Skip 3
    }
    if (i == 7)
    {
        break; // Stop at 7
    }
    Console.WriteLine(i);
}
// Output: 0, 1, 2, 4, 5, 6

// Declaring and initializing arrays
int[] numbers = new int[5]; // Array of 5 integers
string[] names = { "Alice", "Bob", "Charlie" };

// Accessing elements
Console.WriteLine(names[0]); // Alice
Console.WriteLine(names[2]); // Charlie

// Array length
Console.WriteLine($"Array has {names.Length} elements");

// Looping through arrays
int[] scores = { 85, 92, 78, 95, 88 };

// Using for loop
for (int i = 0; i < scores.Length; i++)
{
    Console.WriteLine($"Score {i + 1}: {scores[i]}");
}

// Using foreach loop
foreach (int score in scores)
{
    Console.WriteLine(score);
}

// Modifying array elements
int[] numbers = { 10, 20, 30, 40, 50 };

numbers[0] = 100; // Change first element
numbers[2] = 300; // Change third element

Console.WriteLine(numbers[0]); // 100
Console.WriteLine(numbers[2]); // 300

// Method without parameters or return value
void SayHello()
{
    Console.WriteLine("Hello, World!");
}

// Calling the method
SayHello(); // Output: Hello, World!

// Method with parameters
void Greet(string name)
{
    Console.WriteLine($"Hello, {name}!");
}

Greet("Alice"); // Hello, Alice!
Greet("Bob");   // Hello, Bob!

// Method with return value
int Add(int a, int b)
{
    return a + b;
}

int result = Add(5, 3);
Console.WriteLine(result); // 8

// Using directly
Console.WriteLine(Add(10, 20)); // 30

// Method with multiple parameters and logic
double CalculateAverage(int[] numbers)
{
    int sum = 0;
    foreach (int num in numbers)
    {
        sum += num;
    }
    return (double)sum / numbers.Length;
}

int[] scores = { 85, 90, 78, 92 };
double avg = CalculateAverage(scores);
Console.WriteLine($"Average: {avg}");

// Defining a class
class Person
{
    // Fields
    public string Name;
    public int Age;

    // Method
    public void Introduce()
    {
        Console.WriteLine($"Hi, I'm {Name} and I'm {Age} years old.");
    }
}

// Creating and using objects
class Program
{
    static void Main()
    {
        // Creating objects
        Person person1 = new Person();
        person1.Name = "Alice";
        person1.Age = 25;

        Person person2 = new Person();
        person2.Name = "Bob";
        person2.Age = 30;

        // Using methods
        person1.Introduce();
        person2.Introduce();
    }
}

// Class with constructor
class Car
{
    public string Brand;
    public string Model;
    public int Year;

    // Constructor
    public Car(string brand, string model, int year)
    {
        Brand = brand;
        Model = model;
        Year = year;
    }

    public void DisplayInfo()
    {
        Console.WriteLine($"{Year} {Brand} {Model}");
    }
}

// Usage
Car myCar = new Car("Toyota", "Camry", 2023);
myCar.DisplayInfo(); // 2023 Toyota Camry

// Complete example combining all concepts
using System;

class Student
{
    public string Name;
    public int[] Grades;

    public Student(string name, int[] grades)
    {
        Name = name;
        Grades = grades;
    }

    public double CalculateAverage()
    {
        int sum = 0;
        foreach (int grade in Grades)
        {
            sum += grade;
        }
        return (double)sum / Grades.Length;
    }

    public void DisplayReport()
    {
        double avg = CalculateAverage();
        Console.WriteLine($"Student: {Name}");
        Console.WriteLine($"Average: {avg:F2}");

        if (avg >= 90)
        {
            Console.WriteLine("Grade: A - Excellent!");
        }
        else if (avg >= 80)
        {
            Console.WriteLine("Grade: B - Great job!");
        }
        else if (avg >= 70)
        {
            Console.WriteLine("Grade: C - Good work!");
        }
        else
        {
            Console.WriteLine("Grade: F - Keep trying!");
        }
    }
}

class Program
{
    static void Main()
    {
        int[] aliceGrades = { 95, 88, 92, 85 };
        Student alice = new Student("Alice", aliceGrades);
        alice.DisplayReport();
    }
}
